mapper.update:
update members 
   set nickname=#{nickName}
     , profile=#{profile}
     , updateat= default
where userid=#{userId}

update members 
   set profileurl=#{profileUrl}
     , updateat= default
where userid=#{userId}

update members 
   set profileurl=#{profileUrl}
     , updateat= default
where userid=#{userId}

update members 
   set AUTHORITY = #{role}  
 where  userid = #{userId}

update members 
   set userid = #{userId},
   	   password = #{password},
   	   email = #{email} 
 where  userid = #{userId}

mapper.delete:
delete from members
 where userid = #{userId}
   and password = #{password}
